    /**
     * Evento para el select de plataforma, cada vez que se cambia
     * Oculta todos los campos (menos plataforma)
     * Discrimina y carga los datos de la plataforma que se ha eleguido
     */
    $(".plataforma").change(function() {
        // Obtengo el padre del elemento clicado
        var padre = $(this).parent();
        // Obtengo su ID
        var id_padre = $(padre).attr('id');
        // Oculto todos elmentos ocultar hijos del padre
        $("#" + id_padre + " .ocultar").css("display", "none");
        var opcion = $("#" + id_padre + " .plataforma option:selected").val();
        switch (opcion) {
            case "goo" :
                generar_form(id_padre, idioma, ArraySelectCategoriasGoogle, ArraySelectPaises);
                $("#" + id_padre + " .contenedor_preferencias").css("display", "block");
                $("#" + id_padre + " .contenedor_location").css("display", "block");
                break;
            case "57":
                generar_form(id_padre, idioma, ArraySelectCategoriasYoutube, ArraySelectPaises);
                $("#" + id_padre + " .contenedor_preferencias").css("display", "block");
                $("#" + id_padre + " .contenedor_location").css("display", "block");
                break;
            case "61":
                $("#" + id_padre + " .ocultar").css("display", "block");
                generar_form(id_padre, idioma, ArraySelectCategoriaFacebook, ArraySelectPaises);
                break;
            case "135":
                $("#" + id_padre + " .ocultar").css("display", "block");
                generar_form(id_padre, idioma, ArraySelectCategoriasInstagram, ArraySelectPaises);
                break;
            default:
                return false;
        }
    });
    /**
     * Submit formulario
     */
    $( "#send" ).click(function() {
        var error = false;
        // Almaceno los select que contienen la plataforma
        var select = $(".plataforma");
        // Recorro los selects
        $.each(select, function (i, item) {
            // Consigo el id del select en el que esta
            var id = $(this).attr("id");
            // Consigo el valor del option selecionado
            var valor = $("#" + id + " option:selected").val();
            // False es string, es el valor
            if (valor == "false") {
                error = true;
            }
        });
        if (error == false) {
            $("#imagen-final").attr("src", "img/grafico_comparacion.jpg");
            //$("#formulario").submit();
        }
    });
    /**
     * Genera los select
     * @param idioma
     * @param ArraySelectCategorias
     * @param ArraySelectPaises
     */
    function generar_form(id_padre, idioma, ArraySelectCategorias, ArraySelectPaises) {
        // Limpio los selects
        $("#" + id_padre + " .preferencias").empty();
        $("#" + id_padre + " .location").empty();
        // Preferencias
        var item = ArraySelectCategorias[idioma];
        $.each(item, function (i, item) {
            $("#" + id_padre + " .preferencias").append($('<option>', {
                value: item.value,
                text  : item.texto
            }));
        });
        // Paises
        var item = ArraySelectPaises[idioma];
        $.each(item, function (i, item) {
            $("#" + id_padre + " .location").append($('<option>', {
                value: item.value,
                text  : item.texto
            }));
        });
    }
    /**
     * Consigue los parametros pasados por GET
     * @param sParam
     * @returns {boolean}
     */
    function getUrlParameter(sParam) {
        var sPageURL = decodeURIComponent(window.location.search.substring(1)),
            sURLVariables = sPageURL.split('&'),
            sParameterName,
            i;

        for (i = 0; i < sURLVariables.length; i++) {
            sParameterName = sURLVariables[i].split('=');

            if (sParameterName[0] === sParam) {
                return sParameterName[1] === undefined ? true : sParameterName[1];
            }
        }
    };
